mapper.select:
select p.wo_num, p.manu_name, p.manu_code, p.item_name, p.item_num, p.manu_tocount, p.manu_fail, p.emp_Kname, u.unsto_qty, date_format(manu_date, '%Y-%m-%d %H:%i:%s') manu_date from productstatus p join unstore u on p.wo_num = u.wo_num
p.wo_num like CONCAT('%',#{wo_num},'%') OR p.manu_name like CONCAT('%',#{manu_name},'%') OR p.item_name like CONCAT('%',#{item_name},'%') OR p.emp_Kname like CONCAT('%',#{emp_Kname},'%') OR manu_date between date_format(#{manu_date}, '%Y-%m-%d %H:%i:%s') and date_format(#{manu_date}, '%Y-%m-%d %H:%i:%s')+1
order by manu_date desc;

    	select wo_num, manu_name, manu_code, item_name, item_num, manu_tocount, manu_fail, emp_Kname,
		date_format(manu_date, '%Y-%m-%d %H:%i:%s') manu_date from productstatus
		where manu_date= #{manu_date}
