mapper.select:
      	select count(*) from workorder
      	where 1=1

		 and w.wo_num like CONCAT('%', #{wo_num}, '%')


		 and business_num = #{business_num}


		 and w.wo_date = #{order_date}


		 and c.out_date = #{out_date}

select w.wo_num, w.business_num, w.wo_date, c.out_date, w.wo_state, w.wo_qty, manu_name, w.item_name, e.emp_Kname as wo_empname
from workorder w, employee e, contract c
where w.wo_emp=e.emp_num
and c.business_num=w.business_num

 and w.wo_num like CONCAT('%', #{wo_num}, '%')


 and w.business_num = #{business_num}


 and w.wo_date = #{order_date}


 and c.out_date = #{out_date}

order by wo_date desc
limit #{startRow}, #{pageSize}
