mapper.select:
		select d.fail_num, q.wo_num, q.manu_name, q.manu_code, q.item_name, q.item_num, d.emp_Kname, (q.manu_fail+p.manu_fail) fail_qty, d.fail_reson
    	from productstatus p join qc_status q
    	on p.wo_num = q.wo_num left join defective d on q.wo_num = d.wo_num
    	where q.wo_num = #{wo_num}
    	group by q.emp_Kname, q.manu_fail, q.wo_num;
