mapper.select:
select count(*) from workorder
where 1=1

	and w.wo_num like CONCAT('%', #{wo_num}, '%')


	and business_num = #{business_num}


	and date_format(w.wo_date, '%Y-%m-%d') >=
	#{order_date}


	and date_format(w.wo_date, '%Y-%m-%d') <=
	#{dorder_date}


	and date_format(c.out_date, '%Y-%m-%d') >=
	#{out_date}


	and date_format(c.out_date, '%Y-%m-%d') <=
	#{out_date}

and wo_available=1

select w.wo_num, w.business_num, w.wo_date, date_format(c.out_date, '%Y-%m-%d') as out_date, w.wo_qty, w.manu_name, w.item_name, e.emp_Kname as wo_empname, sum(p.manu_tocount) as pr_sum, (case when sum(p.manu_tocount) < w.wo_qty then "진행중" when sum(p.manu_tocount) = w.wo_qty then "완료" else "대기" end) as wo_state from employee e join workorder w on w.wo_emp=e.emp_num join contract c on w.business_num=c.business_num left outer join productstatus p on p.wo_num=w.wo_num where wo_available=1
and RIGHT(w.wo_num,12) = #{wo_num}
and w.business_num = #{business_num}
and date_format(w.wo_date, '%Y-%m-%d') >= #{order_date}
and date_format(w.wo_date, '%Y-%m-%d') <= #{dorder_date}
and date_format(c.out_date, '%Y-%m-%d') >= #{out_date}
and date_format(c.out_date, '%Y-%m-%d') <= #{out_date}
group by wo_num, business_num, wo_date, out_date, wo_qty, manu_name, item_name, wo_empname order by wo_date desc limit #{startRow}, #{pageSize}
